// define a function that returns an array of process IDs to be killed
function f_gold(pid, ppid, kill) {
    // define a depth-first search function to traverse the process tree
    function dfs(u) {
        ans.push(u);
        for (let v of g[u] ?? []) {
            dfs(v);
        }
    }
    // initialize variables and build the process tree
    let g = {};
    let n = pid.length;
    for (let i = 0; i < n; i++) {
        if (g[ppid[i]] === undefined) {
            g[ppid[i]] = [];
        }
        g[ppid[i]].push(pid[i]);
    }
    // perform the depth-first search starting from the process to be killed
    let ans = [];
    dfs(kill);
    // return the array of process IDs to be killed
    return ans;
}
