```javascript
function find_local_extrema(nums) {
    var previous_index = 0;
    var count = previous_index;
    for (var current_index = 1; current_index < nums.length - 1; current_index++) {
        if (nums[current_index] == nums[current_index + 1]) {
            continue;
        }
        if (nums[current_index] > nums[previous_index] && nums[current_index] > nums[current_index + 1]) {
            count += 1;
        }
        if (nums[current_index] < nums[previous_index] && nums[current_index] < nums[current_index + 1]) {
            count += 1;
        }
        previous_index = current_index;
    }
    return count;
}
