function count_valid_words(sentence) {
    function is_valid_word(token) {
        var hyphen = false;
        for (var i = 0; i < token.length; i++) {
            var character = token[i];
            if (character.match(/^\d+$/) || ('!.,'.includes(character) && i < token.length - 1)) {
                return false;
            }
            if (character == '-') {
                if (hyphen || i == 0 || i == token.length - 1 || token[i - 1].toLowerCase() !== token[i - 1] || token[i + 1].toLowerCase() !== token[i + 1]) {
                    return false;
                }
                hyphen = true;
            }
        }
        return true;
    }
    return sentence.split(/\s+/).map(is_valid_word).reduce((count, is_valid) => count + is_valid, 0);
}
