function fraction_to_decimal(numerator, denominator) {
    if (numerator == 0) {
        return '0';
    }
    var result = [];
    var is_negative = (numerator > 0) ^ (denominator > 0);
    if (is_negative) {
        result.push('-');
    }
    var num = Math.abs(numerator);
    var den = Math.abs(denominator);
    result.push(String(Math.floor(num / den)));
    num %= den;
    if (num == 0) {
        return result.join('');
    }
    result.push('.');
    var map = {};
    while (num != 0) {
        map[num] = result.length;
        num *= 10;
        result.push(String(Math.floor(num / den)));
        num %= den;
        if (num in map) {
            var index = map[num];
            result.splice(index, 0, '(');
            result.push(')');
