function find_number_of_connected_cells(grid) {
    function depth_first_search(i, j) {
        grid[i][j] = 0;
        for (let [row, col] of [[0, -1], [0, 1], [-1, 0], [1, 0]]) {
            let [x, y] = [i + row, j + col];
            if (0 <= x && x < rows && 0 <= y && y < cols && grid[x][y] == 1) {
                depth_first_search(x, y);
            }
        }
    }
    let [rows, cols] = [grid.length, grid[0].length];
    for (let i = 0; i < rows; i++) {
        for (let j = 0; j < cols; j++) {
            if (grid[i][j] == 1 && (i == 0 || i == rows - 1 || j == 0 || j == cols - 1)) {
                depth_first_search(i, j);
            }
        }
    }
    return grid.reduce((a, b) => b.reduce((x, y) => x + y) + a, 0);
}
