// function to convert numbers in nums array using mapping object and sort them
function f_gold(mapping, nums) {
    var m = [];
    // loop through each number in nums array
    for (var i = 0; i < nums.length; i++) {
        var a = nums[i];
        var b = 0;
        var t = 1;
        // loop through each digit of the number
        while (1) {
            var x = a % 10;
            a = Math.floor(a / 10);
            // convert digit using mapping object
            x = mapping[x];
            b = x * t + b;
            t *= 10;
            if (a == 0) {
                break;
            }
        }
        // push converted number, index and original number to m array
        m.push([b, i, nums[i]]);
    }
    // sort m array based on converted number, index and original number
    m.sort((a, b) => a[0] != b[0] ? a[0] - b[0] : (a[1] != b[1] ? a[1] - b[1] : a[2] - b[2]));
    // replace numbers in nums array with sorted numbers
    for (var i = 0; i < m.length; i++) {
        nums[i] = m[i][2];
    }
    // return sorted nums array
    return nums;
}
