from typing import *
def f_gold(n: int, lights: List[List[int]], requirement: List[int]
) -> int:
    d = [0] * 100010
    for p, r in lights:
        i, j = max(0, p - r), min(n - 1, p + r)
        d[i] += 1
        d[j + 1] -= 1
    return sum(s >= r for s, r in zip(accumulate(d), requirement))
