function findShortestPath(maze, entrance) {
    var rows = maze.length;
    var cols = maze[0].length;
    var i = entrance[0];
    var j = entrance[1];
    var queue = [[i, j]];
    maze[i][j] = '+';
    var steps = 0;
    while (queue.length > 0) {
        steps += 1;
        for (var _ = 0, ql = queue.length; _ < ql; _++) {
            [i, j] = queue.shift();
            for (var rowOffset = 0; rowOffset < 2; rowOffset++) {
                for (var colOffset = 0; colOffset < 2; colOffset++) {
                    var x = i + rowOffset;
                    var y = j + colOffset;
                    if (x >= 0 && x < rows && y >= 0 && y < cols && maze[x][y] == '.') {
                        if (x == 0 || x == rows - 1 || y == 0 || y == cols - 1) {
                            return steps;
                        }
                        queue.push([x, y]);
                        maze[x][y] = '+';
                    }
                }
            }
        }
    }
    return -1;
}
