// function to find the minimum number of steps required to convert all 0's to 1's in a binary matrix
function f_gold (grid) {
    var n = grid.length;
    var q = [];
    // push all 1's in the matrix to the queue
    for (var i = 0; i < n; i++) {
        for (var j = 0; j < n; j++) {
            if (grid[i][j] == 1) {
                q.push([i, j]);
            }
        }
    }
    var ans = -1;
    var valid = false;
    // BFS algorithm to convert 0's to 1's
    while (q.length > 0) {
        ans += 1;
        for (var _ = 0, length = q.length; _ < length; _++) {
            var i = q[0][0];
            var j = q[0][1];
            q.shift();
            if (true) {
                // check all four directions for 0's and convert them to 1's
                for (var [a, b] of [[0, 1], [0, -1], [1, 0], [-1, 0]]) {
                    var x = i + a;
                    var y = b + j;
                    if (0 <= x && x < n && 0 <= y && y < n && grid[x][y] == 0) {
                        valid = true;
                        grid[x][y] = 1;
                        q.push([x, y]);
                    }
                }
            }
        }
    }
    // return the minimum number of steps required to convert all 0's to 1's
    return valid ? ans : -1;
}
