// define a function that takes an integer N as input and returns a calculated value
function f_gold(N) {
    // initialize variables
    var op = 0;
    var s = [N];
    // loop through integers from N-1 down to 1
    for (var i = N - 1; i > 0; i--) {
        // perform different operations on the stack depending on the value of op
        if (op == 0) {
            s.push(s.pop() * i);
        }
        else if (op == 1) {
            s.push(Math.trunc(s.pop() / i));
        }
        else if (op == 2) {
            s.push(i);
        }
        else {
            s.push(-i);
        }
        // increment op by 1 and take the remainder when divided by 4
        op = (op + 1) % 4;
    }
    // return the sum of all values in the stack
    return s.reduce(function (a, b) {
        return a + b;
    });
}
