// define a function to check if a given string is a valid palindrome or not
function f_gold(s) {
    // define a helper function to check if a substring is a valid palindrome or not
    function check(i, j) {
        while (i < j) {
            if (s[i] != s[j]) {
                return false;
            }
            [i, j] = [i + 1, j - 1];
        }
        return true;
    }
    // initialize two pointers to the start and end of the string
    var i = 0, j = s.length - 1;
    // iterate through the string until the two pointers meet
    while (i < j) {
        // if the characters at the two pointers are not equal, check if the substring without the left or right character is a valid palindrome or not
        if (s[i] != s[j]) {
            return check(i, j - 1) || check(i + 1, j);
        }
        // move the two pointers towards the center of the string
        [i, j] = [i + 1, j - 1];
    }
    // if the function has not returned yet, the string is a valid palindrome
    return true;
}
