function find_max_subarray_length(nums, k) {
    nums.sort((a, b) => (a - b));
    var max_subarray_length = 1;
    var window_sum = 0;
    var left_pointer = 0;
    var right_pointer = 1;
    var n = nums.length;
    while (right_pointer < n) {
        window_sum += (nums[right_pointer] - nums[right_pointer - 1]) * (right_pointer - left_pointer);
        right_pointer += 1;
        while (window_sum > k) {
            window_sum -= nums[right_pointer - 1] - nums[left_pointer];
            left_pointer += 1;
        }
        max_subarray_length = Math.max(max_subarray_length, right_pointer - left_pointer);
    }
    return max_subarray_length;
}
