// define a function that takes two arrays of scores and ages and returns the maximum possible score
function f_gold(scores, ages) {
    // create a new array of tuples with age and score
    var nums = Array.from(Array(scores.length), (_, k) => [ages[k], scores[k]]);
    // sort the array by age and then by score
    nums.sort((a, b) => a[0] !== b[0] ? a[0] - b[0] : a[1] - b[1]);
    var n = nums.length;
    // create a new array with the same scores as the input array
    var dp = nums.map(function (num) { return num[1]; });
    // loop through all pairs of tuples and update the maximum score for each
    for (var i = 0; i < n; i++) {
        for (var j = 0; j < i; j++) {
            if (nums[i][1] >= nums[j][1]) {
                dp[i] = Math.max(dp[i], dp[j] + nums[i][1]);
            }
        }
    }
    // return the maximum score
    return Math.max.apply(Math, dp);
}
