```javascript
// define a function that performs operations on an array of integers
function f_gold(ops) {
    let stk = [];
    // iterate through each operation in the array
    for (let op of ops) {
        // if the operation is addition, add the last two elements in the stack
        if (op == '+') {
            stk.push(stk[stk.length - 1] + stk[stk.length - 2]);
        }
        // if the operation is doubling, double the last element in the stack
        else if (op == 'D') {
            stk.push(stk[stk.length - 1] << 1);
        }
        // if the operation is cancelling, remove the last element in the stack
        else if (op == 'C') {
            stk.pop();
        }
        // if the operation is an integer, add it to the stack
        else {
            stk.push(parseInt(op));
        }
    }
    // return the sum of all elements in the stack
    return stk.reduce((a, b) => a + b, 0);
}
