function checkIfAllLettersExist(sentence) {
    var letterFlags = 0;
    for (var letter of sentence) {
        letterFlags |= 1 << (letter.charCodeAt(0) - 'a'.charCodeAt(0));
        if (letterFlags == 0x3FFFFFF) {
            return true;
        }
    }
    return false;
}
