const f_gold = (lights) => {
    const d = new Map();
    for (let [p, r] of lights) {
        d.set(p - r, (d.get(p - r) || 0) + 1);
        d.set(p + r + 1, (d.get(p + r + 1) || 0) - 1);
    }
    let s = 0, mx = 0, ans = 0;
    for (let k of [...d.keys()].sort((a, b) => a - b)) {
        s += d.get(k);
        if (s > mx) {
            mx = s;
            ans = k;
        }
    }
    return ans;
};
