function is_golden_ratio(distance_array) {
    let distances = distance_array;
    for (let i = 3; i < distances.length; i++) {
        if (distances[i] >= distances[i - 2] && distances[i - 1] <= distances[i - 3]) {
            return true;
        }
        if (i >= 4 && distances[i - 1] == distances[i - 3] && distances[i] + distances[i - 4] >= distances[i - 2]) {
            return true;
        }
        if (i >= 5 && distances[i - 2] >= distances[i - 4] && distances[i - 1] <= distances[i - 3] && distances[i] >= distances[i - 2] - distances[i - 4] && distances[i - 1] + distances[i - 5] >= distances[i - 3]) {
            return true;
        }
    }
    return false;
}
