function getMaxPrice(m, n, prices) {
    function dfs(h, w) {
        let ans = h in priceMap ? (priceMap[h][w] ?? 0) : 0;
        for (let i = 1; i <= h / 2; i++) {
            ans = Math.max(ans, dfs(i, w) + dfs(h - i, w));
        }
        for (let i = 1; i <= w / 2; i++) {
            ans = Math.max(ans, dfs(h, i) + dfs(h, w - i));
        }
        return ans;
    }
    let priceMap = new Map();
    for (let [height, width, price] of prices) {
        if (!(height in priceMap)) priceMap[height] = {};
        priceMap[height][width] = price;
    }
    return dfs(m, n);
}
