// function to calculate the sum of all subarrays with even length
function f_gold(arr) {
    let n = arr.length;
    // create an array to store prefix sum of the input array
    let presum = new Array(n + 1).fill(0);
    for (let i = 0; i < n; i++) {
        presum[i + 1] = presum[i] + arr[i];
    }
    let res = 0;
    // loop through all subarrays with even length and add their sum to the result
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j += 2) {
            if (i + j < n) {
                res += presum[i + j + 1] - presum[i];
            }
        }
    }
    return res;
}
```
