function find_max_overlap(lights) {
    var distance_map = {};
    for (var _i = 0, lights_1 = lights; _i < lights_1.length; _i++) {
        var _a = lights_1[_i], position = _a[0], radius = _a[1];
        distance_map[position - radius] = (distance_map[position - radius] || 0) + 1;
        distance_map[position + radius + 1] = (distance_map[position + radius + 1] || 0) - 1;
    }
    var sum = 0;
    var max_sum = 0;
    var answer = 0;
    for (var [key, value] of Object.entries(distance_map).sort((a, b) => (parseInt(a[0]) - parseInt(b[0])))) {
        sum += value;
        if (sum > max_sum) {
            max_sum = sum;
            answer = parseInt(key);
        }
    }
    return answer;
}
