function areEquationsSatisfied(equations) {
    function find(parent) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }
    let parent = Array.from(Array(26).keys());
    for (let equation of equations) {
        let left = equation.charCodeAt(0) - 'a'.charCodeAt(0);
        let right = equation.charCodeAt(equation.length - 1) - 'a'.charCodeAt(0);
        if (equation[1] == '=') {
            parent[find(left)] = find(right);
        }
    }
    for (let equation of equations) {
        let left = equation.charCodeAt(0) - 'a'.charCodeAt(0);
        let right = equation.charCodeAt(equation.length - 1) - 'a'.charCodeAt(0);
        if (equation[1] == '!' && find(left) == find(right)) {
            return false;
        }
    }
