const { default: defaultdict } = require("defaultdict");

function f_gold(arr, difference) {
    const dp = new defaultdict(Number);
    let ans = 1;
    for (let num of arr) {
        dp[num] = dp[num - difference] + 1;
        ans = Math.max(ans, dp[num]);
    }
    return ans;
}
