```javascript
function find_k_subsequence_sums(code, k) {
    var code_length = code.length;
    var result = new Array(code_length).fill(0);
    if (k == 0) {
        return result;
    }
    for (var i = 0; i < code_length; i++) {
        if (k > 0) {
            for (var j = i + 1; j < i + k + 1; j++) {
                result[i] += code[j % code_length];
            }
        }
        else {
            for (var j = i + k; j < i; j++) {
                result[i] += code[(j + code_length) % code_length];
            }
        }
    }
    return result;
}
