function findMaxSatisfiedCustomers(customers, grumpy, minutes) {
    var totalUnsatisfiedCustomers = 0;
    for (var i = 0; i < customers.length; i++) {
        totalUnsatisfiedCustomers += customers[i] * grumpy[i];
    }
    var totalCustomers = 0;
    for (var i = 0; i < customers.length; i++) {
        totalCustomers += customers[i];
    }
    var unsatisfiedCustomersInWindow = 0;
    var maxSatisfiedCustomers = 0;
    for (var i = 1; i <= customers.length; i++) {
        unsatisfiedCustomersInWindow += customers[i - 1] * grumpy[i - 1];
        if (i - minutes >= 0) {
            maxSatisfiedCustomers = Math.max(maxSatisfiedCustomers, totalCustomers - (totalUnsatisfiedCustomers - unsatisfiedCustomersInWindow));
            unsatisfiedCustomersInWindow -= customers[i - minutes] * grumpy[i - minutes];
        }
    }
    return maxSatisfiedCustomers;
}
```
