```javascript
function find_k_largest_numbers(nums, k) {
    var indices = Array.from(Array(nums.length).keys());
    indices.sort(function (i, j) { return nums[i] - nums[j]; });
    return indices.slice(-k).sort((a, b) => (a - b)).map(i => nums[i]);
}
