function checkSimilarity(sentence1, sentence2, similarPairs) {
    if (sentence1.length != sentence2.length) {
        return false;
    }
    var similarPairsSet = new Set();
    for (var i = 0; i < similarPairs.length; i++) {
        similarPairsSet.add([similarPairs[i][0], similarPairs[i][1]].join(","));
    }
    for (var i = 0; i < sentence1.length; i++) {
        var isSimilar = (similarPairsSet.has([sentence1[i], sentence2[i]].join(",")) || similarPairsSet.has([sentence2[i], sentence1[i]].join(",")) || sentence1[i] == sentence2[i]);
        if (!isSimilar) {
            return false;
        }
    }
    return true;
}
