```javascript
function f_gold(source_string, target_string) {
    var source_length = source_string.length;
    var target_length = target_string.length;
    var repetitions_needed = Math.ceil(target_length / source_length);
    var repeated_source = new Array(repetitions_needed).fill(source_string);
    for (var i = 0; i < 3; i++) {
        if (repeated_source.join("").indexOf(target_string) != -1) {
            return repetitions_needed;
        }
        repetitions_needed += 1;
        repeated_source.push(source_string);
    }
    return -1;
}
```
