function find_common_intervals(first_interval_list, second_interval_list) {
    var i = 0, j = 0;
    var common_intervals = [];
    while (i < first_interval_list.length && j < second_interval_list.length) {
        var start_interval_1 = first_interval_list[i][0];
        var end_interval_1 = first_interval_list[i][1];
        var start_interval_2 = second_interval_list[j][0];
        var end_interval_2 = second_interval_list[j][1];
        var left = Math.max(start_interval_1, start_interval_2);
        var right = Math.min(end_interval_1, end_interval_2);
        if (left <= right) {
            common_intervals.push([left, right]);
        }
        if (end_interval_1 < end_interval_2) {
            i += 1;
