const accumulate = (arr, initial) => arr.reduce((acc, curr) => [...acc, curr + acc[acc.length - 1]], [initial]).slice(1);

function f_gold(piles, k) {
    const presum = piles.map(p => accumulate(p, 0));
    const dp = new Array(k + 1).fill(0);
    for (const s of presum) {
        for (let j = k; j >= 0; j--) {
            for (let idx = 0; idx < s.length; idx++) {
                const v = s[idx];
                if (j >= idx) {
                    dp[j] = Math.max(dp[j], dp[j - idx] + v);
                }
            }
        }
    }
    return dp[dp.length - 1];
}
