function f_gold(nums) {
    var j = 0; // initialize a variable j to 0
    var ans = j; // initialize a variable ans to j
    for (var i = 1; i < nums.length - 1; i++) { // loop through the array from index 1 to second last index
        if (nums[i] == nums[i + 1]) { // if the current number is equal to the next number, skip to the next iteration
            continue;
        }
        if (nums[i] > nums[j] && nums[i] > nums[i + 1]) { // if the current number is greater than both its adjacent numbers, increment ans by 1
            ans += 1;
        }
        if (nums[i] < nums[j] && nums[i] < nums[i + 1]) { // if the current number is smaller than both its adjacent numbers, increment ans by 1
            ans += 1;
        }
        j = i; // set j to i
    }
    return ans; // return the final value of ans
}
