// function to count the number of pairs of adjacent elements in an array that are equal
function f_gold(nums) {
    let n = nums.length;
    let i = 0, ans = 0;
    while (i < n - 1) {
        if (nums[i] == nums[i + 1]) { // check if adjacent elements are equal
            ans += 1; // increment the count of pairs
            i += 1; // move to the next pair
        }
        else {
            i += 2; // move to the next pair
        }
    }
    if ((n - ans) % 2) { // check if there is an odd number of non-paired elements
        ans += 1; // increment the count of pairs
    }
    return ans; // return the count of pairs
}
```
