function find_maximum_sum_difference(nums1, nums2) {
    function find_difference(nums1, nums2) {
        var difference = nums1.map((a, i) => a - nums2[i]);
        var current_sum = difference[0], max_sum = difference[0];
        for (var value of difference.slice(1)) {
            if (current_sum > 0) {
                current_sum += value;
            }
            else {
                current_sum = value;
            }
            max_sum = Math.max(max_sum, current_sum);
        }
        return max_sum;
    }
    var sum1 = nums1.reduce(function(a, b) {
        return a + b;
    });
    var sum2 = nums2.reduce(function(a, b) {
        return a + b;
    });
    return Math.max(sum2 + find_difference(nums1, nums2), sum1 + find_difference(nums2, nums1));
}
