function find_minimum_speed(distance_array, hours) {
    function can_arrive_on_time(speed) {
        var total_time = 0;
        for (var i = 0; i < distance_array.length; i++) {
            total_time += (i == distance_array.length - 1) ? (distance_array[i] / speed) : Math.ceil(distance_array[i] / speed);
        }
        return total_time <= hours;
    }
    var left = 1;
    var right = 10 ** 7;
    while (left < right) {
        var mid = (left + right) >> 1;
        if (can_arrive_on_time(mid)) {
            right = mid;
        }
        else {
            left = mid + 1;
        }
    }
    return can_arrive_on_time(left) ? left : -1;
}
