function transform_numbers(nums, operations) {
    var num_index_map = {};
    for (var i = 0; i < nums.length; i++) {
        num_index_map[nums[i]] = i;
    }
    for (var i = 0; i < operations.length; i++) {
        var old_num = operations[i][0];
        var new_num = operations[i][1];
        var idx = num_index_map[old_num];
        delete num_index_map[old_num];
        num_index_map[new_num] = idx;
    }
    var transformed_nums = [];
    for (var i = 0; i < nums.length; i++) {
        transformed_nums.push(0);
    }
    for (var num in num_index_map) {
        transformed_nums[num_index_map[num]] = parseInt(num);
    }
    return transformed_nums;
}
