function isValid(code) {
    function check(tag) {
        return 1 <= tag.length && tag.length <= 9 && [...tag].every(c => c === c.toUpperCase());
    }
    let stk = [];
    let i = 0, n = code.length;
    while (i < n) {
        if (i && !stk.length) {
            return false;
        }
        if (code.slice(i, i + 9) === '<![CDATA[') {
            i = code.indexOf(']]>', i + 9);
            if (i < 0) {
                return false;
            }
            i += 2;
        } else if (code.slice(i, i + 2) === '</') {
            let j = i + 2;
            i = code.indexOf('>', j);
            if (i < 0) {
                return false;
            }
            let t = code.slice(j, i);
            if (!check(t) || !stk.length || stk.pop() !== t) {
                return false;
            }
        } else if (code[i] === '<') {
            let j = i + 1;
            i = code.indexOf('>', j);
            if (i < 0) {
                return false;
            }
            let t = code.slice(j, i);
            if (!check(t)) {
                return false;
            }
            stk.push(t);
        }
        i += 1;
    }
    return !stk.length;
}
