// function to distribute arrows among targets
function f_gold(numArrows, aliceArrows) {
    let n = aliceArrows.length;
    let state = 0;
    let mx = -1;
    // loop through all possible combinations of targets
    for (var mask = 0; mask < (1 << n); mask++) {
        let cnt = 0, points = 0;
        // calculate the number of arrows and points for each combination
        for (let i = 0; i < aliceArrows.length; i++) {
            if ((mask >> i) & 1) {
                cnt += aliceArrows[i] + 1;
                points += i;
            }
        }
        // update the state with the combination that maximizes points and uses <= numArrows arrows
        if (cnt <= numArrows && mx < points) {
            state = mask;
            mx = points;
        }
    }
    // distribute arrows according to the selected combination
    var ans = Array(aliceArrows.length).fill(0);
    for (let i = 0; i < aliceArrows.length; i++) {
        if ((state >> i) & 1) {
            ans[i] = aliceArrows[i] + 1;
            numArrows -= ans[i];
        }
    }
    // distribute remaining arrows to the first target
    ans[0] = numArrows;
    return ans;
}
