// define a function that takes in three parameters: m, n, and ops
function f_gold(m, n, ops) {
    // loop through each pair of values in ops and update m and n to be the minimum of m and a, and n and b respectively
    for (let [a, b] of ops) {
        m = Math.min(m, a);
        n = Math.min(n, b);
    }
    // return the product of m and n
    return m * n;
}
