// function to check if all rooms in a building can be visited starting from room 0
function f_gold(rooms) {
    // depth-first search function to visit all connected rooms
    function dfs(u) {
        // if the room has already been visited, return
        if (vis.has(u)) {
            return;
        }
        // mark the room as visited
        vis.add(u);
        // recursively visit all connected rooms
        for (let v of rooms[u]) {
            dfs(v);
        }
    }
    // create a set to keep track of visited rooms
    let vis = new Set();
    // start the search from room 0
    dfs(0);
    // return true if all rooms have been visited, false otherwise
    return vis.size == rooms.length;
}
