def f_gold(s: str, k: int) -> int:
    counter = [0] * 26
    i = j = maxCnt = 0
    while i < len(s):
        counter[ord(s[i]) - ord('A')] += 1
        maxCnt = max(maxCnt, counter[ord(s[i]) - ord('A')])
