function largestTriangleArea(points) {
    let ans = 0;
    for (let [x1, y1] of points) {
        for (let [x2, y2] of points) {
            for (let [x3, y3] of points) {
                let u1 = x2 - x1, v1 = y2 - y1;
                let u2 = x3 - x1, v2 = y3 - y1;
                let t = Math.abs(u1 * v2 - u2 * v1) / 2;
                ans = Math.max(ans, t);
            }
        }
    }
    return ans;
}
