function apply_discount(sentence, discount_percentage) {
    var result = [];
    for (var word of sentence.split(/\s+/)) {
        if (word[0] == '$' && /^\d+$/.test(word.slice(1))) {
            var discounted_price = parseInt(word.substring(1)) * (1 - discount_percentage / 100);
            result.push('$' + discounted_price.toFixed(2));
        }
        else {
            result.push(word);
        }
    }
    return result.join(' ');
}
