// function to find two numbers, one from aliceSizes and one from bobSizes, whose difference is half of the total difference between the sum of aliceSizes and bobSizes
function f_gold(aliceSizes, bobSizes) {
    // calculate the difference between the sum of aliceSizes and bobSizes, and divide by 2
    var diff = (aliceSizes.reduce((a, b) => a + b) - bobSizes.reduce((a, b) => a + b)) >> 1;
    // create a set of bobSizes
    var s = new Set(bobSizes);
    // iterate through aliceSizes
    for (var a of aliceSizes) {
        // calculate the target number that alice needs to find in bobSizes
        var target = a - diff;
        // check if the target number exists in bobSizes
        if (s.has(target)) {
            // return the two numbers whose difference is half of the total difference between the sum of aliceSizes and bobSizes
            return [a, target];
        }
    }
}
