const findMissingAndDuplicateNumbers = (numbers) => {
    let xorSum = 0;
    let n = numbers.length;
    for (let i = 1; i < n + 1; i++) {
        xorSum ^= i ^ numbers[i - 1];
    }
    let diff = xorSum & (~xorSum + 1);
    let missingNumber = 0;
    for (let i = 1; i < n + 1; i++) {
        if ((numbers[i - 1] & diff) == 0) {
            missingNumber ^= numbers[i - 1];
        }
        if ((i & diff) == 0) {
            missingNumber ^= i;
        }
    }
    let duplicateNumber = xorSum ^ missingNumber;
    for (let num of numbers) {
        if (missingNumber == num) {
            return [missingNumber, duplicateNumber];
        }
    }
    return [duplicateNumber, missingNumber];
}
