// function to find the largest sum of two numbers in an array that is less than k
function f_gold(nums, k) {
    // sort the array in ascending order
    nums.sort((a, b) => (a - b));
    // initialize pointers to the start and end of the array
    var low = 0;
    var high = nums.length - 1;
    // initialize the result to -1
    var res = -1;
    // loop until the pointers meet
    while (low < high) {
        // calculate the sum of the current pair of numbers
        var val = nums[low] + nums[high];
        // if the sum is less than k, update the result and move the low pointer to the right
        if (val < k) {
            res = Math.max(res, val);
            low += 1;
        }
        // if the sum is greater than or equal to k, move the high pointer to the left
        else {
            high -= 1;
        }
    }
    // return the result
    return res;
}
