function rearrangeWords(sentence) {
    var words = sentence.split(/\s+/);
    var result = [];
    for (var i = 0; i < words.length; i++) {
        var currentWord = words[i];
        if (currentWord.toLowerCase()[0] != 'a' && currentWord.toLowerCase()[0] != 'e' && currentWord.toLowerCase()[0] != 'i' && currentWord.toLowerCase()[0] != 'o' && currentWord.toLowerCase()[0] != 'u') {
            currentWord = currentWord.substring(1) + currentWord[0];
        }
        currentWord += 'ma';
        currentWord += 'a'.repeat(i + 1);
        result.push(currentWord);
    }
    return result.join(' ');
}
