function find_maximum_gold_coins(n) {
    if (n == 0) {
        return 0;
    }
    var coins = new Array(n + 1).fill(0);
    coins[1] = 1;
    for (var i = 2; i <= n; i++) {
        coins[i] = (i % 2 == 0) ? coins[i >> 1] : coins[i >> 1] + coins[(i >> 1) + 1];
    }
    return Math.max(...coins);
}
