function f_gold(distance) {
    let d = distance;
    for (let i = 3; i < d.length; i++) {
        // check if the current distance and the two previous distances can form a triangle
        if (d[i] >= d[i - 2] && d[i - 1] <= d[i - 3]) {
            return true;
        }
        // check if the current distance and the distance four positions before can form a triangle
        if (i >= 4 && d[i - 1] == d[i - 3] && d[i] + d[i - 4] >= d[i - 2]) {
            return true;
        }
        // check if the current distance and the distances two and four positions before can form a triangle
        if (i >= 5 && d[i - 2] >= d[i - 4] && d[i - 1] <= d[i - 3] && d[i] >= d[i - 2] - d[i - 4] && d[i - 1] + d[i - 5] >= d[i - 3]) {
            return true;
        }
    }
