// function to calculate the maximum XOR value of a subarray of nums
// nums: an array of integers
// maximumBit: an integer representing the maximum number of bits in the binary representation of the integers in nums
const f_gold = (nums, maximumBit) => {
    let n = nums.length;
    let s = new Array(n + 1).fill(0);
    // calculate the XOR prefix sum of nums
    for (let i = 0; i < nums.length; i++) {
        s[i + 1] = s[i] ^ nums[i];
    }
    let ans = [];
    // calculate the maximum XOR value for each subarray of nums in reverse order
    for (let x of s.slice(1).reverse()) {
        let t = 0;
        for (let i = 0; i < maximumBit; i++) {
            if (((x >> i) & 1) == 0) {
                t |= 1 << i;
            }
        }
        ans.push(t);
    }
    return ans;
}
