// function to find the maximum length of ribbons that can be cut from given ribbons to get at least k ribbons of equal length
function f_gold(ribbons, k) {
    var low = 0;
    var high = 100000;
    while (low < high) {
        // calculate the middle value of low and high
        var mid = (low + high + 1) >> 1;
        var cnt = 0;
        // count the number of ribbons that can be cut from each ribbon of given ribbons
        for (var ribbon of ribbons) {
            cnt += Math.floor(ribbon / mid);
        }
        // if the count is less than k, update high to mid - 1
        if (cnt < k) {
            high = mid - 1;
        }
        // else update low to mid
        else {
            low = mid;
        }
    }
    // return the maximum length of ribbons that can be cut from given ribbons to get at least k ribbons of equal length
    return low;
}
```
