```javascript
// function to calculate the nth term of a sequence
function f_gold(n) {
    // initialize the first 5 terms of the sequence
    var dp = [1, 1, 1, 1, 1];
    var MOD = 1000000007;
    // calculate the next n-1 terms of the sequence
    for (var _ = 0; _ < n - 1; _++) {
        dp = [
            (dp[1] + dp[2] + dp[4]) % MOD,
            (dp[0] + dp[2]) % MOD,
            (dp[1] + dp[3]) % MOD,
            dp[2],
            (dp[2] + dp[3]) % MOD,
        ];
    }
    // return the sum of all n terms of the sequence
    return dp.reduce((a, b) => a + b) % MOD;
}
